// Preparar parámetros para consulta
const today = new Date();
const currentWeekISO = getWeekISO(today.toISOString().split('T')[0]);

function getWeekISO(dateStr) {
  if (!dateStr) return null;
  const date = new Date(dateStr + 'T00:00:00Z');
  const year = date.getUTCFullYear();
  const jan4 = new Date(Date.UTC(year, 0, 4));
  const jan4Day = jan4.getUTCDay() || 7;
  const firstMonday = new Date(Date.UTC(year, 0, 4 - jan4Day + 1));
  const daysDiff = Math.floor((date - firstMonday) / 86400000);
  let weekNumber = Math.floor(daysDiff / 7) + 1;
  let isoYear = year;

  if (daysDiff < 0) {
    const prevYear = year - 1;
    const prevJan4 = new Date(Date.UTC(prevYear, 0, 4));
    const prevJan4Day = prevJan4.getUTCDay() || 7;
    const prevFirstMonday = new Date(Date.UTC(prevYear, 0, 4 - prevJan4Day + 1));
    const prevDaysDiff = Math.floor((date - prevFirstMonday) / 86400000);
    weekNumber = Math.floor(prevDaysDiff / 7) + 1;
    isoYear = prevYear;
  } else if (weekNumber === 53) {
    const nextJan4 = new Date(Date.UTC(year + 1, 0, 4));
    const nextJan4Day = nextJan4.getUTCDay() || 7;
    const nextFirstMonday = new Date(Date.UTC(year + 1, 0, 4 - nextJan4Day + 1));
    const daysUntilNextYear = Math.floor((nextFirstMonday - date) / 86400000);
    if (daysUntilNextYear < 7) {
      isoYear = year + 1;
      weekNumber = 1;
    }
  }

  return `${isoYear}-W${String(weekNumber).padStart(2, '0')}`;
}

return [{
  json: {
    week_iso: currentWeekISO,
    snapshot_date: today.toISOString().split('T')[0]
  }
}];

// Obtener headline y copy desde Meta API si es necesario
// Por ahora, extraeremos de ad_name o usaremos valores por defecto
const items = $input.all();

const enrichedItems = items.map(item => {
  const adName = item.json.ad_name || '';

  // Intentar extraer headline y copy del nombre del ad
  // Formato común: "Headline - Copy" o similar
  const parts = adName.split(' - ');
  const headline = parts[0] || adName.slice(0, 30);
  const copy = parts.slice(1).join(' - ') || adName.slice(30) || 'No copy available';

  return {
    json: {
      ...item.json,
      headline: headline.trim(),
      copy: copy.trim()
    }
  };
});

return enrichedItems;

// Separar top10 y bottom10 por CTR
const items = $input.all();

if (items.length === 0) {
  return [{
    json: {
      top10: [],
      bottom10: [],
      total_ads: 0
    }
  }];
}

// Ordenar por CTR descendente
const sorted = items.sort((a, b) => {
  const ctrA = parseFloat(a.json.avg_ctr || 0);
  const ctrB = parseFloat(b.json.avg_ctr || 0);
  return ctrB - ctrA;
});

// Top 10
const top10 = sorted.slice(0, 10).map((item, index) => ({
  ad_id: item.json.ad_id,
  ad_name: item.json.ad_name,
  creative_url: item.json.creative_url,
  permalink_url: item.json.permalink_url,
  headline: item.json.headline,
  copy: item.json.copy,
  ctr: parseFloat((item.json.avg_ctr || 0).toFixed(2)),
  roas: parseFloat((item.json.avg_roas || 0).toFixed(2)),
  cpa: parseFloat((item.json.avg_cpa || 0).toFixed(2)),
  impressions: parseInt(item.json.total_impressions || 0),
  clicks: parseInt(item.json.total_clicks || 0),
  spend: parseFloat((item.json.total_spend || 0).toFixed(2)),
  days_active: parseInt(item.json.days_active || 0),
  ranking_position: index + 1
}));

// Bottom 10
const bottom10 = sorted.slice(-10).reverse().map((item, index) => ({
  ad_id: item.json.ad_id,
  ad_name: item.json.ad_name,
  creative_url: item.json.creative_url,
  permalink_url: item.json.permalink_url,
  headline: item.json.headline,
  copy: item.json.copy,
  ctr: parseFloat((item.json.avg_ctr || 0).toFixed(2)),
  roas: parseFloat((item.json.avg_roas || 0).toFixed(2)),
  cpa: parseFloat((item.json.avg_cpa || 0).toFixed(2)),
  impressions: parseInt(item.json.total_impressions || 0),
  clicks: parseInt(item.json.total_clicks || 0),
  spend: parseFloat((item.json.total_spend || 0).toFixed(2)),
  days_active: parseInt(item.json.days_active || 0),
  ranking_position: index + 1
}));

const weekData = $('Prepare Week ISO').item.json;

return [{
  json: {
    week_iso: weekData.week_iso,
    snapshot_date: weekData.snapshot_date,
    top10: top10,
    bottom10: bottom10,
    total_ads: sorted.length
  }
}];

// Preparar datos para insertar top10
const item = $input.first().json;
const weekISO = item.week_iso;
const snapshotDate = item.snapshot_date;

const top10Records = item.top10.map(rank => ({
  json: {
    week_iso: weekISO,
    snapshot_date: snapshotDate,
    ranking_type: 'top10',
    ad_id: rank.ad_id,
    ad_name: rank.ad_name,
    creative_url: rank.creative_url,
    permalink_url: rank.permalink_url || null,
    headline: rank.headline,
    copy: rank.copy,
    ctr: rank.ctr,
    roas: rank.roas,
    cpa: rank.cpa,
    impressions: rank.impressions,
    clicks: rank.clicks,
    spend: rank.spend,
    conversions: null, // Se puede calcular si hay datos de conversiones
    ranking_position: rank.ranking_position,
    metadata: JSON.stringify({
      days_active: rank.days_active
    })
  }
}));

return top10Records;

// Preparar datos para insertar bottom10
const item = $input.first().json;
const weekISO = item.week_iso;
const snapshotDate = item.snapshot_date;

const bottom10Records = item.bottom10.map(rank => ({
  json: {
    week_iso: weekISO,
    snapshot_date: snapshotDate,
    ranking_type: 'bottom10',
    ad_id: rank.ad_id,
    ad_name: rank.ad_name,
    creative_url: rank.creative_url,
    permalink_url: rank.permalink_url || null,
    headline: rank.headline,
    copy: rank.copy,
    ctr: rank.ctr,
    roas: rank.roas,
    cpa: rank.cpa,
    impressions: rank.impressions,
    clicks: rank.clicks,
    spend: rank.spend,
    conversions: null,
    ranking_position: rank.ranking_position,
    metadata: JSON.stringify({
      days_active: rank.days_active
    })
  }
}));

return bottom10Records;

// Preparar respuesta final esperando que ambos inserts terminen
const rankingsData = $('Calculate Rankings').item.json;
const weekData = $('Prepare Week ISO').item.json;

return [{
  json: {
    success: true,
    message: `✅ Rankings calculados y guardados para ${weekData.week_iso}`,
    week_iso: weekData.week_iso,
    snapshot_date: weekData.snapshot_date,
    top10: rankingsData.top10,
    bottom10: rankingsData.bottom10,
    total_ads: rankingsData.total_ads,
    generated_at: new Date().toISOString()
  }
}];
