// Transformar datos de Meta Insights al formato de Supabase
const items = $input.all();
const transformedItems = [];

for (const item of items) {
  const data = item.json.data || [];

  for (const insight of data) {
    // Obtener fecha del insight
    const date = insight.date_start || new Date().toISOString().split('T')[0];

    // Extraer acciones y conversiones
    const actions = insight.actions || [];
    const conversions = actions
      .filter(action => action.action_type === 'purchase' || action.action_type === 'lead')
      .reduce((sum, action) => sum + parseInt(action.value || 0), 0);

    // Calcular revenue (asumiendo valor promedio por conversi√≥n)
    // Puedes ajustar esto seg√∫n tu modelo de negocio
    const revenuePerConversion = 200; // Valor promedio por conversi√≥n
    const revenue = conversions * revenuePerConversion;

    // Calcular m√©tricas derivadas
    const impressions = parseInt(insight.impressions || 0);
    const clicks = parseInt(insight.clicks || 0);
    const spend = parseFloat(insight.spend || 0);

    const ctr = impressions > 0 ? ((clicks / impressions) * 100).toFixed(2) : 0;
    const cpa = conversions > 0 ? (spend / conversions).toFixed(2) : 0;
    const roas = spend > 0 ? (revenue / spend).toFixed(2) : 0;

    // Extraer destination del ad_name o campaign_name
    // Puedes ajustar la l√≥gica seg√∫n c√≥mo identifiques destinations
    let destination = null;
    if (insight.ad_name) {
      const destMatch = insight.ad_name.match(/\[(.*?)\]/);
      if (destMatch) {
        destination = destMatch[1];
      }
    }

    // Extraer angle del ad_name
    let angle = null;
    if (insight.ad_name) {
      // Buscar patrones comunes de angles en el nombre
      const angleKeywords = ['Crecimiento', 'ROI', 'Transformaci√≥n', 'Estrategia', 'Servicios', 'Soluciones'];
      for (const keyword of angleKeywords) {
        if (insight.ad_name.includes(keyword)) {
          angle = keyword;
          break;
        }
      }
    }

    // Extraer format del ad_name o determinar por tipo de ad
    let format = null;
    if (insight.ad_name) {
      const formatMatch = insight.ad_name.match(/(image|video|carousel|single_image)/i);
      if (formatMatch) {
        format = formatMatch[1].toLowerCase();
      }
    }

    const transformedItem = {
      json: {
        ad_id: insight.ad_id || `ad_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
        ad_name: insight.ad_name || 'Unknown Ad',
        campaign_name: insight.campaign_name || null,
        destination: destination,
        angle: angle,
        format: format,
        impressions: impressions,
        clicks: clicks,
        spend: parseFloat(spend.toFixed(2)),
        conversions: conversions,
        revenue: parseFloat(revenue.toFixed(2)),
        ctr: parseFloat(ctr),
        cpa: parseFloat(cpa),
        roas: parseFloat(roas),
        date: date
      }
    };

    transformedItems.push(transformedItem);
  }
}

return transformedItems;

